<?php/**		pyrobot will reply to its name with a random message**		@author 	Pyrokid*		@copyright	None*		@category	IRC bot module*		@package	pyrobot*/class RandomReply_m51 extends PB2 {	var $ln;		function __construct() {		parent::PB2();		$this->ln[1] = "What?";		$this->ln[2] = "What do you want, sNickname?";		$this->ln[3] = "I'm on break.";		$this->ln[4] = "Gtfo, plz.";		$this->ln[5] = "I am not available, please try again never.";		$this->ln[6] = "How may I not be of service?";		$this->ln[7] = "If you called, I'm not answering.";		$this->ln[8] = "Piss off, sNickname.";		$this->ln[9] = "Please leave a message after the beep.";		$this->ln[10] = "I'm too busy being a servant.";		$this->ln[11] = "sNickname, you're an idiot.";		$this->ln[12] = "sNickname, you're not very smart.";		$this->ln[13] = "sNickname, you're a ass.";		$this->ln[14] = "Screw off, sNickname.";		$this->ln[15] = "I can't talk right now sNickname, your mother is on the other line.";		$this->ln[16] = "sNickname, why are you such a moron?";		$this->ln[17] = "Now I know why you have that huge bump on your head.";		$this->ln[18] = "Leave me alone!!";		return 1;	}	function randString($nick) {		$rand = $this->ln[mt_rand(1, count($this->ln))];		$rand = str_replace('sNickname', $nick, $rand);		return $rand;	}	function onUserChat($nick, $channel, $text, $host) {		$cmd = explode(' ', $text);		$arr = array('pyrobot', 'pyrobot,', 'pyrobot:', 'pyrobot.', 'pyrobot-', 'pyrobot;');		if(in_array(strtolower($cmd[0]), $arr)) {			if(PB2::$aStatic['bSilent'] == 1) {				$this->send("NOTICE ".$nick." :4/!\ Notice: I am in silent mode");				return 1;			}			$this->send("PRIVMSG " . $channel . " :". $this->randString(PB2::safeNick($nick)));			return 1;		}		return 0;	}	function onUserJoin($nick, $channel, $host) {		return 1;	}	function __destruct() { return 1; }	public function __call($name, $arguments) { return 1; }}?>